console.log("확인하기");
$(function(){
    $(".prod_button_area > .button_wish").click(function(){
        console.log("확인하기");
        console.log(this);   

        if($(this).hasClass("red")){
            var imageUrl = "data:image/svg+xml;base64,";
            $(this).find("span").css("backgroundImage", "url('" + imageUrl +"')");
            $(this).removeClass("red");
        } else {
            var imageUrl = "data:image/svg+xml;base64,";
            $(this).find("span").css("backgroundImage", "url('" + imageUrl +"')");
            $(this).addClass("red");
        }
    });
});